mapper.select:
select
id
,nickname
,avatar
,phone
,gender
,birth
,address
,integral
,balance
,role
,enable_state
,comment
from app_user


        and id=#{id}


        and nickname=#{nickname}


        and nickname like concat('%',#{nickname_like},'%')


        and phone=#{phone}


        and phone like concat('%',#{phone_like},'%')


        and gender=#{gender}


        and birth <= #{birth_lt}


        and birth >= #{birth_gt}


        and role=#{role}


        and enable_state=#{enableState}



    order by ${sortByField} ${sortByWay}
