mapper.select:
select
id
,name
,age
,score
,gender
,state
,interest
,birth
,regist_time
,avatar
,photos
,attachment
,introduction
,detail
,phone
,location
,lng
,lat
,file_input
from sys_demo


        and id=#{id}


        and name=#{name}


        and name like concat('%',#{name_like},'%')


        and age=#{age}


        and score=#{score}


        and gender=#{gender}


        and state=#{state}


        and interest=#{interest}


        and interest like concat('%',#{interest_like},'%')


        and birth=#{birth}


        and birth <= #{birth_lt}


        and birth >= #{birth_gt}


        and regist_time=#{registTime}


        and regist_time <= #{registTime_lt}


        and regist_time >= #{registTime_gt}


        and avatar=#{avatar}


        and avatar like concat('%',#{avatar_like},'%')


        and photos=#{photos}


        and photos like concat('%',#{photos_like},'%')


        and attachment=#{attachment}


        and attachment like concat('%',#{attachment_like},'%')


        and introduction=#{introduction}


        and introduction like concat('%',#{introduction_like},'%')


        and detail=#{detail}


        and detail like concat('%',#{detail_like},'%')


        and phone=#{phone}


        and phone like concat('%',#{phone_like},'%')


        and location=#{location}


        and location like concat('%',#{location_like},'%')


        and lng=#{lng}


        and lat=#{lat}


        and file_input=#{fileInput}


        and file_input like concat('%',#{fileInput_like},'%')



    order by ${sortByField} ${sortByWay}
