mapper.select:
select
    id
    ,type
    ,title
    ,picture
    ,introduction
    ,content_type
    ,content_id
    ,link_url
    ,location
from app_article


        and id=#{id}


        and type=#{type}


        and title=#{title}


        and title like concat('%',#{title_like},'%')


        and picture=#{picture}


        and picture like concat('%',#{picture_like},'%')


        and introduction=#{introduction}


        and introduction like concat('%',#{introduction_like},'%')


        and content_type=#{contentType}


        and content_id=#{contentId}


        and link_url=#{linkUrl}


        and link_url like concat('%',#{linkUrl_like},'%')


        and location=#{location}


        and location like concat('%',#{location_like},'%')



    order by ${sortByField} ${sortByWay}
