mapper.select:
select
    id
    ,type
    ,title
    ,picture
    ,introduction
    ,content_type
    ,content_id
    ,link_url
    ,location
from app_article


        and id=#{id}


        and type=#{type}


        and title=#{title}


        and title like concat('%',#{title_like},'%')


        and content_type=#{contentType}


        and content_id=#{contentId}



    order by ${sortByField} ${sortByWay}
