mapper.select:
select
    id
    ,title
    ,picture
    ,type
    ,target_id
    ,create_time
    ,update_time
from app_banner


        and id=#{id}


        and title=#{title}


        and title like concat('%',#{title_like},'%')


        and picture=#{picture}


        and picture like concat('%',#{picture_like},'%')


        and type=#{type}


        and target_id=#{targetId}


        and create_time=#{createTime}


        and create_time <= #{createTime_lt}


        and create_time >= #{createTime_gt}


        and update_time=#{updateTime}


        and update_time <= #{updateTime_lt}


        and update_time >= #{updateTime_gt}



    order by ${sortByField} ${sortByWay}
