mapper.select:
select
    id
    ,title
    ,method
    ,account
    ,request_url
    ,request_ip
    ,request_body
    ,request_header
    ,response
    ,state
    ,message
    ,times
    ,request_time
from sys_admin_log


        and id=#{id}


        and title=#{title}


        and title like concat('%',#{title_like},'%')


        and account=#{account}


        and account like concat('%',#{account_like},'%')


        and request_ip=#{requestIp}


        and request_ip like concat('%',#{requestIp_like},'%')


        and state=#{state}


        and times >= #{times_gt}


        and request_time <= #{requestTime_lt}


        and request_time >= #{requestTime_gt}



    order by ${sortByField} ${sortByWay}
