mapper.select:
select
    id
    ,account
    ,ip
    ,location
    ,browser
    ,os
    ,message
    ,login_time
    ,state
    ,online_flag
    ,logout_type
    ,logout_time
    ,token
from sys_login_log


        and id=#{id}


        and account=#{account}


        and account like concat('%',#{account_like},'%')


        and ip=#{ip}


        and ip like concat('%',#{ip_like},'%')


        and location=#{location}


        and location like concat('%',#{location_like},'%')


        and login_time <= #{loginTime_lt}


        and login_time >= #{loginTime_gt}


        and state=#{state}


        and online_flag=#{onlineFlag}


        and logout_type=#{logoutType}


        and logout_time <= #{logoutTime_lt}


        and logout_time >= #{logoutTime_gt}



    order by ${sortByField} ${sortByWay}
