mapper.select:
select
id,
title,
method,
account,
request_url,
request_ip,
request_body,
request_header,
response,
state,
message,
times,
request_time
from sys_admin_log


        and id=#{po.id}


        and title=#{po.title}


        and title like concat(#{page.params.title_like},'%')


        and account=#{po.account}


        and account like concat(#{page.params.account_like},'%')


        and request_ip=#{po.requestIp}


        and request_ip like concat(#{page.params.requestIp_like},'%')


        and state=#{po.state}


        and times >= #{page.params.times_gt}


        and request_time <= #{page.params.requestTime_lt}


        and request_time >= #{page.params.requestTime_gt}



    order by ${page.sortByField} ${page.sortByWay}
