mapper.select:
select
    id,
    pid,
    type,
    name,
    sort,
    link_name,
    link_phone,
    link_email,
    state,
    create_user,
    create_time,
    update_user,
    update_time
from sys_organization


        and id=#{po.id}


        and pid=#{po.pid}


        and type=#{po.type}


        and name=#{po.name}


        and name like concat(#{page.params.name_like},'%')


        and sort=#{po.sort}


        and link_name=#{po.linkName}


        and link_name like concat(#{page.params.linkName_like},'%')


        and link_phone=#{po.linkPhone}


        and link_phone like concat(#{page.params.linkPhone_like},'%')


        and link_email=#{po.linkEmail}


        and link_email like concat(#{page.params.linkEmail_like},'%')


        and state=#{po.state}



    order by ${page.sortByField} ${page.sortByWay}

select * from sys_organization where ancestors like concat(#{ancestors},',%')
and state=#{state}
order by sort
