mapper.select:
select
    id,
    pid,
    type,
    name,
    sort,
    link_name,
    link_phone,
    link_email,
    state,
    creator,
    create_time,
    updator,
    update_time
from sys_organization


        and id=#{po.id}


        and pid=#{po.pid}


        and type=#{po.type}


        and name=#{po.name}


        and name like concat(#{page.params.name_like},'%')


        and sort=#{po.sort}


        and link_name=#{po.linkName}


        and link_name like concat(#{page.params.linkName_like},'%')


        and link_phone=#{po.linkPhone}


        and link_phone like concat(#{page.params.linkPhone_like},'%')


        and link_email=#{po.linkEmail}


        and link_email like concat(#{page.params.linkEmail_like},'%')


        and state=#{po.state}


        and creator=#{po.creator}


        and creator like concat(#{page.params.creator_like},'%')


        and create_time=#{po.createTime}


        and create_time <= #{page.params.createTime_lt}


        and create_time >= #{page.params.createTime_gt}


        and updator=#{po.updator}


        and updator like concat(#{page.params.updator_like},'%')


        and update_time=#{po.updateTime}


        and update_time <= #{page.params.updateTime_lt}


        and update_time >= #{page.params.updateTime_gt}



    order by ${page.sortByField} ${page.sortByWay}
