mapper.select:
select
id,
account,
password,
phone,
nickname,
avatar,
email,
gender,
state,
system_flag,
create_user,
create_time,
update_user,
update_time,
remark
from sys_user


        and id=#{po.id}


        and account=#{po.account}


        and account like concat(#{page.params.account_like},'%')


        and phone=#{po.phone}


        and phone like concat(#{page.params.phone_like},'%')


        and nickname=#{po.nickname}


        and nickname like concat(#{page.params.nickname_like},'%')


        and gender=#{po.gender}


        and state=#{po.state}


        and system_flag=#{po.systemFlag}


        and create_time <= #{page.params.createTime_lt}


        and create_time >= #{page.params.createTime_gt}


        and update_time <= #{page.params.updateTime_lt}


        and update_time >= #{page.params.updateTime_gt}



    order by ${page.sortByField} ${page.sortByWay}
