mapper.select:
select
    id,
    name,
    age,
    score,
    gender,
    state,
    interest,
    birth,
    regist_time,
    avatar,
    video,
    photos,
    attachment,
    introduction,
    detail,
    phone,
    location,
    lng,
    lat,
    file_input,
    user_id,
    org_id
from sys_demo

        deleted=0

        and id=#{po.id}


        and name=#{po.name}


        and name like concat(#{page.params.name_like},'%')


        and age=#{po.age}


        and score=#{po.score}


        and gender=#{po.gender}


        and state=#{po.state}


        and interest=#{po.interest}


        and birth=#{po.birth}


        and birth <= #{page.params.birth_lt}


        and birth >= #{page.params.birth_gt}


        and regist_time=#{po.registTime}


        and avatar=#{po.avatar}


        and avatar like concat(#{page.params.avatar_like},'%')


        and video=#{po.video}


        and video like concat(#{page.params.video_like},'%')


        and photos=#{po.photos}


        and photos like concat(#{page.params.photos_like},'%')


        and attachment=#{po.attachment}


        and attachment like concat(#{page.params.attachment_like},'%')


        and introduction=#{po.introduction}


        and introduction like concat(#{page.params.introduction_like},'%')


        and detail=#{po.detail}


        and detail like concat(#{page.params.detail_like},'%')


        and phone=#{po.phone}


        and phone like concat(#{page.params.phone_like},'%')


        and location=#{po.location}


        and location like concat(#{page.params.location_like},'%')


        and lng=#{po.lng}


        and lat=#{po.lat}


        and file_input=#{po.fileInput}


        and file_input like concat(#{page.params.fileInput_like},'%')


        and user_id=#{po.userId}


        and org_id=#{po.orgId}



    order by ${page.sortByField} ${page.sortByWay}
